	function SINO(cual) {
   var elElemento=document.getElementById(cual);
   if(elElemento.style.left == '0px') {
      elElemento.style.left = '-2000px';
   } else {
      elElemento.style.left = '0px';
   }
};
function SIN(cual1) {
   var elElemento=document.getElementById(cual1);
   if(elElemento.style.backgroundColor == 'rgb(204, 204, 204)') {
      elElemento.style.backgroundColor = 'rgb(241, 241, 241)';
   } else {
      elElemento.style.backgroundColor = 'rgb(204, 204, 204)';
   }
}
function NO(cual1) {
   var elElemento=document.getElementById(cual1);
   if(elElemento.style.right == '50px') {
      elElemento.style.right = '-2000px';
   } else {
      elElemento.style.right = '50px';
   }
}


